mapper.select:
select count(*) from emoji_history where cancel_yn =
'Y' and confirm_yn =
'N';

mapper.update:
UPDATE main_emoji SET name = #{name}, price =
#{price} , file = #{file}
WHERE id = #{id};

UPDATE main_emoji set file = #{file}, name = #{name},
price = #{price},
update_at = now() WHERE id = #{id} ;
