mapper.select:
SELECT f.* , m.name as mainCategoryName FROM FREE_BOARD f inner join board_main_category m on f.main_category = m.id WHERE U_ID = #{userId}
and main_category = #{mainCategory} and sub_category = #{subCategory}
order by f.id limit #{start} , 10

		select s.* from board_sub_category s
		right join free_board f
		on f.sub_category = s.id
		where u_id = #{userId} and s.group_id = #{groupId}
        group by s.id;
