mapper.select:
select count(*) from ( select f.* , ifnull(count(r.good_yn) , 0) as goodCount , ifnull(v.view , 0) as viewCount from free_board f inner join board_main_category m on f.main_category = m.id inner join board_sub_category s on f.sub_category = s.id left join free_board_recommendation r on r.free_board_id = f.id and r.good_yn = 'Y' left join free_board_view v on v.free_board_id = f.id where f.delete_yn = 'N'
and (f.main_category = #{mainCategory} and f.sub_category = #{subCategory})
group by f.id ) as count ;

SELECT f.* , m.name as mainCategoryName FROM FREE_BOARD f inner join board_main_category m on f.main_category = m.id WHERE U_ID = #{userId}
and f.main_category = #{mainCategory} and f.sub_category = #{subCategory}
and f.delete_yn = 'N'
order by f.id limit #{start} , 10

SELECT count(*) FROM FREE_BOARD f inner join board_main_category m on f.main_category = m.id WHERE U_ID = #{userId}
and (f.main_category = #{mainCategory} and f.sub_category = #{subCategory})
and f.delete_yn = 'N'

